import React, {useState} from 'react';

import './ExpensesFilter.css';

const ExpensesFilter = (props) => {
 
    const[enteredYear, setEnteredYear] = useState('');

    const yearHandler = (event) =>{
        event.preventdefault();
        console.log(event.target.value);
        setEnteredYear(event.target.value);
        props.onChooseYear(enteredYear);
    };
    return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select>
          <option value='2022' onClick={yearHandler}>2022</option>
          <option value='2021' onClick={yearHandler}>2021</option>
          <option value='2020' onClick={yearHandler}>2020</option>
          <option value='2019' onClick={yearHandler}>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
